import React from "react";
import Router from "next/router";
import { Formik, Form } from "formik";
import * as Yup from "yup";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import PersonRoundedIcon from "@material-ui/icons/PersonRounded";
import LockRoundedIcon from "@material-ui/icons/LockRounded";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import { FormikField } from "./common/FormikField";
import { ToastContainer, toast } from "react-toastify";

const useStyles = makeStyles({
  root: {
    background: "#ECECEC",
  },
});

interface FormValues {
  username: string;
  password: string;
}
interface LoginFormProps {
  header: string;
  route: string;
}

const SignupSchema = Yup.object().shape({
  username: Yup.string()
    .min(3, "Too short!")
    .max(30, "Too long!")
    .required("Required!"),
  password: Yup.string()
    .min(4, "Too short!")
    .max(30, "Too long!")
    .required("Required!"),
});

export const LoginForm: React.FC<LoginFormProps> = ({ header, route }) => {
  const classes = useStyles();
  const theme = useTheme();
  const initialValues: FormValues = {
    username: "",
    password: "",
  };

  const handleSubmit = (values: FormValues): void => {
    // Call server HERE
    if (values.username === "yes") {
      Router.push(`${route}`);
    } else {
      const options = { style: { background: theme.palette.error.main } };
      toast.error("Invalid username or password.", options);
    }
  };

  return (
    <Box padding={4} maxWidth="sm" className={classes.root}>
      <Typography component="h1" variant="h5" style={{ textAlign: "left" }}>
        {header}
      </Typography>
      <Formik
        initialValues={initialValues}
        onSubmit={handleSubmit}
        validationSchema={SignupSchema}
      >
        {({ dirty, isValid, handleReset }) => {
          return (
            <Form>
              {" "}
              <FormikField
                name="username"
                label="Username"
                icon={<PersonRoundedIcon />}
              />
              <FormikField
                name="password"
                label="Password"
                type="password"
                icon={<LockRoundedIcon />}
              />
              <Button
                variant="contained"
                color="primary"
                disabled={!dirty || !isValid}
                type="submit"
                fullWidth
              >
                Submit
              </Button>
              <ToastContainer />
            </Form>
          );
        }}
      </Formik>
    </Box>
  );
};
