import React, { useRef, useLayoutEffect } from "react";
/* Imports for amCharts*/
// Have to add nextjs transpiler
// https://github.com/amcharts/amcharts4/issues/1043
import * as am4core from "@amcharts/amcharts4/core";
import * as am4charts from "@amcharts/amcharts4/charts";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
// Types
import { ServeData } from "../../util/types";

interface ServePictorialProps {
  data: ServeData;
}

const ServePictorial: React.FC<ServePictorialProps> = ({ data }) => {
  const chartFinal = useRef(null);

  useLayoutEffect(() => {
    // ... Chart code
    // Themes begin
    am4core.useTheme(am4themes_animated);
    // Themes end

    let iconPath =
      "M 28.00,24.00C 28.00,17.44 31.94,11.82 37.58,9.3434.02,6.04 29.26,4.00 24.00,4.0018.74,4.00 13.98,6.04 10.42,9.3416.06,11.82 20.00,17.44 20.00,24.0020.00,30.56 16.06,36.18 10.42,38.6613.98,41.96 18.74,44.00 24.00,44.0029.26,44.00 34.02,41.96 37.58,38.6631.94,36.18 28.00,30.56 28.00,24.00 ZM 40.38,12.56C 35.54,14.10 32.00,18.64 32.00,24.0032.00,29.36 35.54,33.90 40.38,35.4442.66,32.20 44.00,28.26 44.00,24.0044.00,19.74 42.66,15.80 40.38,12.56 ZM 7.62,12.56C 5.34,15.80 4.00,19.74 4.00,24.004.00,28.26 5.34,32.20 7.62,35.4412.46,33.90 16.00,29.36 16.00,24.0016.00,18.64 12.46,14.10 7.62,12.56 ZM 48.00,0.00C 48.00,0.00 48.00,48.00 48.00,48.0048.00,48.00 0.00,48.00 0.00,48.000.00,48.00 0.00,0.00 0.00,0.000.00,0.00 48.00,0.00 48.00,0.00 ZM 48.00,0.00C 48.00,0.00 48.00,48.00 48.00,48.0048.00,48.00 0.00,48.00 0.00,48.000.00,48.00 0.00,0.00 0.00,0.000.00,0.00 48.00,0.00 48.00,0.00 Z";

    let chart = am4core.create("chartdiv", am4charts.SlicedChart);
    chart.hiddenState.properties.opacity = 0; // this makes initial fade in effect
    chart.paddingLeft = 150;

    chart.data = data;

    let series = chart.series.push(new am4charts.PictorialStackedSeries());
    series.dataFields.value = "value";
    series.dataFields.category = "name";
    series.alignLabels = true;
    // this makes only A label to be visible
    series.labels.template.propertyFields.disabled = "disabled";
    series.ticks.template.propertyFields.disabled = "disabled";

    series.maskSprite.path = iconPath;
    series.ticks.template.locationX = 1;
    series.ticks.template.locationY = 0;

    series.labelsContainer.width = 100;

    chart.legend = new am4charts.Legend();
    chart.legend.position = "top";
    chart.legend.paddingRight = 160;
    chart.legend.paddingBottom = 40;
    let marker = chart.legend.markers.template.children.getIndex(0);
    chart.legend.markers.template.width = 40;
    chart.legend.markers.template.height = 40;
    // Doesn't work for some reason.
    // marker.cornerRadius(20,20,20,20);

    chartFinal.current = chart;

    return () => {
      chart.dispose();
    };
  }, []);

  // When the data prop changes it will update the chart
  useLayoutEffect(() => {
    chartFinal.current.data = data;
  }, [data]);

  return <div id="chartdiv" style={{ width: "100%", height: "500px" }}></div>;
};

export default ServePictorial;
