// Code adapted from the MUI documentation.
import React, { ReactNode } from "react";
import SwipeableViews from "react-swipeable-views";
import { makeStyles, Theme, useTheme } from "@material-ui/core/styles";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";
import TrendingUpRoundedIcon from "@material-ui/icons/TrendingUpRounded";
import SportsBaseballRoundedIcon from "@material-ui/icons/SportsBaseballRounded";

interface TabPanelProps {
  children?: React.ReactNode;
  dir?: string;
  index: any;
  value: any;
}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`full-width-tabpanel-${index}`}
      aria-labelledby={`full-width-tab-${index}`}
      {...other}
    >
      {value === index && <Box p={3}>{children}</Box>}
    </div>
  );
}

function a11yProps(index: any) {
  return {
    id: `full-width-tab-${index}`,
    "aria-controls": `full-width-tabpanel-${index}`,
  };
}

const useStyles = makeStyles((theme: Theme) => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 500,
  },
}));

interface StatsTabProps {
  tab1Content: ReactNode;
  tab2Content: ReactNode;
}

export const StatsTab: React.FC<StatsTabProps> = ({
  tab1Content,
  tab2Content,
}) => {
  const classes = useStyles();
  const theme = useTheme();
  const [value, setValue] = React.useState(0);

  const handleChange = (event: React.ChangeEvent<{}>, newValue: number) => {
    setValue(newValue);
  };

  const handleChangeIndex = (index: number) => {
    setValue(index);
  };

  return (
    <div className={classes.root}>
      <Tabs
        value={value}
        onChange={handleChange}
        indicatorColor="primary"
        textColor="primary"
        variant="fullWidth"
        aria-label="Statistics and games panel"
      >
        <Tab
          label="Statistics"
          icon={<TrendingUpRoundedIcon />}
          {...a11yProps(0)}
        />
        <Tab
          label="Games"
          icon={<SportsBaseballRoundedIcon />}
          {...a11yProps(1)}
        />
      </Tabs>
      <SwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={value}
        onChangeIndex={handleChangeIndex}
      >
        <TabPanel value={value} index={0} dir={theme.direction}>
          {tab1Content}
        </TabPanel>
        <TabPanel value={value} index={1} dir={theme.direction}>
          {tab2Content}
        </TabPanel>
      </SwipeableViews>
    </div>
  );
};
