import React, { ReactElement } from "react";
import Link from "next/link";
import {
  Menu,
  MenuItem,
  ListItemIcon,
  ListItemText,
  useTheme,
} from "@material-ui/core";
import { Turn as Hamburger } from "hamburger-react";

export type MuiMenuItem = [string, ReactElement, string];

interface MuiMenuProps {
  items: MuiMenuItem[];
  color: string;
  size?: number;
}

export const MuiMenu: React.FC<MuiMenuProps> = ({
  items,
  size = 32,
  color,
}) => {
  // Boiler-plate code from MUI for the menu
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const handleClick = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  // Allows you to refer to your defined theme.
  const theme = useTheme();
  const themeColors = [
    theme.palette.primary.main,
    theme.palette.secondary.main,
    theme.palette.error.main,
    theme.palette.info.main,
    theme.palette.success.main,
  ];
  let numColors = themeColors.length;

  return (
    <React.Fragment>
      <Hamburger
        size={size}
        color={color}
        label="Show menu"
        rounded
        onToggle={(toggled) => {
          if (toggled) {
            // open menu
            handleClick;
            console.log("Menu should be open.");
          } else {
            // close menu
            console.log("Menu should be closed");
            handleClose;
          }
        }}
      />
      <Menu
        id="customized-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        {items.map((curItem, index) => {
          <Link href={curItem[2]} passHref key={index}>
            <MenuItem
              component="a"
              style={{ backgroundColor: themeColors[index % numColors] }}
            >
              <ListItemIcon>{curItem[1]}</ListItemIcon>
              <ListItemText primary={curItem[0]} />
            </MenuItem>
          </Link>;
        })}
      </Menu>
    </React.Fragment>
  );
};
