// Adapted from MUI Documentation
import React from "react";
import MaterialTable, { Column } from "material-table";
// Imports to so that material-table icons are working.
// Also see https://github.com/mbrn/material-table/issues/93#issuecomment-457015457
import { SvgIconProps } from "@material-ui/core/SvgIcon";
import Search from "@material-ui/icons/Search";
import ViewColumn from "@material-ui/icons/ViewColumn";
import SaveAlt from "@material-ui/icons/SaveAlt";
import ChevronLeft from "@material-ui/icons/ChevronLeft";
import ChevronRight from "@material-ui/icons/ChevronRight";
import FirstPage from "@material-ui/icons/FirstPage";
import LastPage from "@material-ui/icons/LastPage";
import Add from "@material-ui/icons/Add";
import Check from "@material-ui/icons/Check";
import FilterList from "@material-ui/icons/FilterList";
import Remove from "@material-ui/icons/Remove";

const tableIcons = {
  Check: () => (<Check />) as React.ReactElement<SvgIconProps>,
  Export: () => (<SaveAlt />) as React.ReactElement<SvgIconProps>,
  Filter: () => (<FilterList />) as React.ReactElement<SvgIconProps>,
  FirstPage: () => (<FirstPage />) as React.ReactElement<SvgIconProps>,
  LastPage: () => (<LastPage />) as React.ReactElement<SvgIconProps>,
  NextPage: () => (<ChevronRight />) as React.ReactElement<SvgIconProps>,
  PreviousPage: () => (<ChevronLeft />) as React.ReactElement<SvgIconProps>,
  Search: () => (<Search />) as React.ReactElement<SvgIconProps>,
  ThirdStateCheck: () => (<Remove />) as React.ReactElement<SvgIconProps>,
  ViewColumn: () => (<ViewColumn />) as React.ReactElement<SvgIconProps>,
  DetailPanel: () => (<ChevronRight />) as React.ReactElement<SvgIconProps>,
};

interface Row {
  blueTeam: string;
  redTeam: string;
  score: string;
  serve: number;
  date: Date;
}

interface TableState {
  columns: Array<Column<Row>>;
  data: Row[];
}

interface GameTableProps {}

export const GameTable: React.FC<GameTableProps> = () => {
  const [state, setState] = React.useState<TableState>({
    columns: [
      { title: "Blue Team", field: "blueTeam" },
      { title: "Red Team", field: "redTeam" },
      { title: "Score", field: "score", type: "numeric" },
      {
        title: "Team with Serve",
        field: "serve",
        lookup: { 0: "Blue", 1: "Red" },
      },
      { title: "Date", field: "date", type: "datetime" },
    ],
    data: [
      {
        blueTeam: "Raph, Alex",
        redTeam: "Val, Son",
        score: "21:10",
        serve: 0,
        date: new Date(),
      },
    ],
  });

  return (
    <MaterialTable
      title="Your Games"
      icons={tableIcons}
      columns={state.columns}
      data={state.data}
      editable={{
        onRowAdd: (newData) =>
          new Promise((resolve) => {
            resolve();
            setState((prevState) => {
              const data = [...prevState.data];
              data.push(newData);
              return { ...prevState, data };
            });
          }),
        onRowDelete: (oldData) =>
          new Promise((resolve) => {
            resolve();
            setState((prevState) => {
              const data = [...prevState.data];
              data.splice(data.indexOf(oldData), 1);
              return { ...prevState, data };
            });
          }),
      }}
    />
  );
};
