import React, { useState } from "react";
import List from "@material-ui/core/List";
import Grid from "@material-ui/core/Grid";
import Fab from "@material-ui/core/Fab";
import Tooltip from "@material-ui/core/Tooltip";
import GroupAddRoundedIcon from "@material-ui/icons/GroupAddRounded";
import { makeStyles } from "@material-ui/core/styles";
import Swal from "sweetalert2";
import { GroupListItem } from "./common/GroupListItem";

const handleRenameGroup = (groupname: string) => {
  console.log("Rename this group", groupname);
};

const handleRenamePlayer = (groupname: string, player: string) => {
  console.log("Rename this player", groupname, player);
};

export interface Group {
  groupname: string;
  players: string[];
}

const useStyles = makeStyles((theme) => ({
  root: {
    width: "100vw",
    // maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
  fab: {
    position: "fixed",
    bottom: theme.spacing(4),
    backgroundColor: theme.palette.success.main,
  },
}));

export const GroupList: React.FC = () => {
  // Use style
  const classes = useStyles();

  // For testing porpuses.
  const group1: Group = {
    groupname: "Group1",
    players: ["Player 1.1", "Player 1.2", "Player 1.3", "Player 1.4"],
  };
  const group2: Group = {
    groupname: "Group2",
    players: ["Player 2.1", "Player 2.2", "Player 2.3", "Player 2.4"],
  };
  // Use state hook
  const initialGroups: Group[] = [group1, group2];
  const [groups, setGroups] = useState(initialGroups);

  // Add a group
  const handleAdd = async () => {
    let newGroup: Group = {
      groupname: "",
      players: [],
    };

    const PlayerSwal = Swal.mixin({
      title: "New Player",
      text: "Names must be unique within groups.",
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "Next &rarr;",
      footer: "Leave box empty to stop adding players.",
      input: "text",
      inputPlaceholder: "New Name",
      inputValidator: (value) => {
        for (let player of newGroup.players) {
          if (player === value) {
            return "Name is not unique!";
          }
        }
      },
      progressSteps: ["1", "2", "3"],
      currentProgressStep: "1",
    });

    const { value: newGroupname } = await Swal.fire({
      title: "New Group Name",
      text: "Must be a unique groupname.",
      icon: "question",
      showCancelButton: true,
      confirmButtonText: "Next &rarr;",
      input: "text",
      inputPlaceholder: "New group name",
      inputValidator: (value) => {
        if (!value) {
          return "Enter a name.";
        }
        for (let group of groups) {
          if (group.groupname === value) {
            return "Name is not unique!";
          }
        }
      },
      progressSteps: ["1", "2", "3"],
      currentProgressStep: "0",
    });
    if (!newGroupname) {
      return;
    }
    newGroup.groupname = `${newGroupname}`;

    let { value: newPlayer } = await PlayerSwal.fire();
    while (newPlayer) {
      newGroup.players.push(`${newPlayer}`);
      const { value: temp } = await PlayerSwal.fire();
      newPlayer = temp;
    }

    Swal.fire({
      title: `Group: ${newGroup.groupname}`,
      text: `Players: ${newGroup.players}`,
      icon: "success",
      confirmButtonText: "Create group",
      showCancelButton: true,
      progressSteps: ["1", "2", "3"],
      currentProgressStep: "2",
      preConfirm: () => {
        // If everything went well.
        if (newGroup.groupname !== "" && newGroup.players.length >= 4) {
          setGroups((groups) => [...groups, newGroup]);
          // Call server here.
        } else {
          Swal.fire({
            title: "Something went wrong.",
            icon: "error",
          });
        }
      },
    });
  };

  // Delete a group
  const handleDelete = (groupname: string) => {
    Swal.fire({
      title: "Are you sure?",
      text: `You won't be able to revert deleting ${groupname}!`,
      icon: "warning",
      showCancelButton: true,
      confirmButtonText: "Yes, delete it!",
      focusConfirm: false,
      focusCancel: true,
      cancelButtonText: "No, cancel!",
    }).then((result) => {
      if (result.value) {
        // Save old state
        const prevGroups = [...groups];
        // Update UI.
        setGroups(
          groups.filter((curGroup) => curGroup.groupname !== groupname)
        );
        console.log("Handle delete.", groupname, groups);
        // Call backend and revert if error.
        if (true) {
          Swal.fire({
            title: "Something went wrong!",
            text: "The group was already deleted.",
            icon: "error",
            toast: true,
            position: "center",
            showConfirmButton: false,
            timer: 4000,
            timerProgressBar: true,
          });
          setGroups(prevGroups);
        }
      }
    });
  };

  return (
    <React.Fragment>
      <List component="nav" className={classes.root}>
        {groups.map((group) => {
          return (
            <GroupListItem
              key={group.groupname}
              group={group}
              onDelete={handleDelete}
              onRenameGroup={handleRenameGroup}
              onRenamePlayer={handleRenamePlayer}
            />
          );
        })}
      </List>
      <Grid
        container
        spacing={0}
        justify="center"
        style={{ minWidth: "100vw" }}
      >
        <Grid item>
          <Tooltip title="New Group" arrow placement="top">
            <Fab
              size="large"
              className={classes.fab}
              aria-label="add"
              onClick={handleAdd}
            >
              <GroupAddRoundedIcon />
            </Fab>
          </Tooltip>
        </Grid>
      </Grid>
    </React.Fragment>
  );
};
